// import pptxgen from "pptxgenjs";
const pptxgen = require ('pptxgenjs')
export default function Slides() {
  // const jsonArray = JSON.parse(jsonStirng);
  // console.log(jsonArray);

  let pres = new pptxgen();
  let slide = pres.addSlide();
  // let ar1 = jsonArray[0];
  // Background Options
  slide.background = { fill: "FFCC00" }; // Example: Orange background

  // Heading
  slide.addText("This is my slide 1", {
    // x: 1, y: 0.5,     // Inches
    // fontSize: 24,
    // bold: true,
    // color: '000000'    // Black text
    x: 0,
    y: 1,
    w: "100%",
    h: 2,
    align: "center",
    color: "0088CC",
    fill: "F1F1F1",
    fontSize: 24,
  });

  // Bullet Points
  slide.addText(
    [
      { text: "Bullet Point 1", options: { bullet: true } },
      { text: "Bullet Point 2", options: { bullet: true } },
      { text: "Bullet Point 3", options: { bullet: true } },
    ],
    {
      x: 1,
      y: 2, // Inches
      fontSize: 18,
      color: "000000", // Black text
      indentLevel: 0, // No indentation for bullets
    }
  );

  pres.writeFile("MyPresentation.pptx");
  // 1. Create a Presentation
}

Slides();
