import pptxgen from "pptxgenjs";

export default function Slides(jsonStr) {
  const slidesData = JSON.parse(jsonStr);
  console.log(jsonArray);
  let pres = new pptxgen();

  slidesData.forEach((slideInfo) => {
      let slide = pres.addSlide();

      // Heading
      slide.addText(slideInfo.title, { 
          x: 1, y: 0.5, 
          fontSize: 20, bold: true, color: '000000' 
      });

      // Bullet Points
      slide.addText(slideInfo.content, { 
          x: 1, y: 1.5, 
          fontSize: 16, color: '000000', 
          bullet: true, indentLevel: 0
      });
  });

