import pptxgen from "pptxgenjs";

export default function Slides(jsonStr) {
  const slidesData = JSON.parse(jsonStr);
  console.log(jsonArray);
  let pres = new pptxgen();

  // Slide Master Definition
  let slideMaster = pres.defineSlideMaster({
    title: 'Master Slide - Akbar',
    background: { fill: 'F1F1F1' }, 
    objects: [
      { 'rect': { x: 0, y: 0.5, w: '100%', h: 0.75, fill: { color: '003366' } } }, 
      { 'text': { 
                  text: 'Akbar the Great', 
                  options: { x: 0.5, y: 0.6, w: 9.0, h: 0.6, align: 'center', fontSize: 20, color: 'FFFFFF' }}
      }
    ] 
  });

  // Create Slides from Data
  slidesData.forEach((slideInfo) => {
    let slide = pres.addSlide({ masterName: 'Master Slide - Akbar' });

    // Heading
    slide.addText(slideInfo.title, { 
        x: 1, y: 1.2, // Adjust position if needed
        fontSize: 20, bold: true, color: '003366'  
    });

    // Bullet Points
    slide.addText(slideInfo.content, { 
        x: 1, y: 2, // Adjust position if needed
        fontSize: 16, color: '003366', // Match header color
        bullet: true, indentLevel: 0
    });
  });

  // Generate Presentation File
  pres.writeFile("AkbarPresentation.pptx"); 
